PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "migrations" ("id" integer primary key autoincrement not null, "migration" varchar not null, "batch" integer not null);
CREATE TABLE IF NOT EXISTS "users" ("id" integer primary key autoincrement not null, "name" varchar not null, "email" varchar not null, "email_verified_at" datetime, "password" varchar not null, "remember_token" varchar, "created_at" datetime, "updated_at" datetime, "role" varchar not null default 'staff', "profile_photo_path" varchar);
CREATE TABLE IF NOT EXISTS "password_reset_tokens" ("email" varchar not null, "token" varchar not null, "created_at" datetime, primary key ("email"));
CREATE TABLE IF NOT EXISTS "sessions" ("id" varchar not null, "user_id" integer, "ip_address" varchar, "user_agent" text, "payload" text not null, "last_activity" integer not null, primary key ("id"));
CREATE TABLE IF NOT EXISTS "cache" ("key" varchar not null, "value" text not null, "expiration" integer not null, primary key ("key"));
CREATE TABLE IF NOT EXISTS "cache_locks" ("key" varchar not null, "owner" varchar not null, "expiration" integer not null, primary key ("key"));
CREATE TABLE IF NOT EXISTS "jobs" ("id" integer primary key autoincrement not null, "queue" varchar not null, "payload" text not null, "attempts" integer not null, "reserved_at" integer, "available_at" integer not null, "created_at" integer not null);
CREATE TABLE IF NOT EXISTS "job_batches" ("id" varchar not null, "name" varchar not null, "total_jobs" integer not null, "pending_jobs" integer not null, "failed_jobs" integer not null, "failed_job_ids" text not null, "options" text, "cancelled_at" integer, "created_at" integer not null, "finished_at" integer, primary key ("id"));
CREATE TABLE IF NOT EXISTS "failed_jobs" ("id" integer primary key autoincrement not null, "uuid" varchar not null, "connection" text not null, "queue" text not null, "payload" text not null, "exception" text not null, "failed_at" datetime not null default CURRENT_TIMESTAMP);
CREATE TABLE IF NOT EXISTS "rooms" ("id" integer primary key autoincrement not null, "title" varchar not null, "slug" varchar not null, "subtitle" varchar, "description" text, "amenities" text, "price_per_night" numeric not null default '0', "capacity" integer not null default '2', "bed_type" varchar, "featured_image" varchar, "gallery_urls" text, "is_active" tinyint(1) not null default '1', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "reviews" ("id" integer primary key autoincrement not null, "guest_name" varchar not null, "rating" integer not null default '5', "comment" text not null, "source" varchar, "is_published" tinyint(1) not null default '1', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "gallery_images" ("id" integer primary key autoincrement not null, "title" varchar not null, "category" varchar not null default 'villa', "image_url" varchar not null, "description" text, "is_featured" tinyint(1) not null default '0', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "landmarks" ("id" integer primary key autoincrement not null, "title" varchar not null, "description" text, "distance" varchar, "map_link" varchar, "image_url" varchar, "category" varchar not null default 'culture', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "content_settings" ("id" integer primary key autoincrement not null, "key" varchar not null, "value" text, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "reservations" ("id" integer primary key autoincrement not null, "room_id" integer, "guest_name" varchar not null, "email" varchar not null, "phone" varchar, "check_in" date not null, "check_out" date not null, "guests" integer not null default ('1'), "arrival_time" varchar, "message" text, "status" varchar not null default ('pending'), "created_at" datetime, "updated_at" datetime, "total_price" numeric not null default ('0'), "discount_percentage" numeric not null default '0', "discount_status" varchar not null default 'none', "discount_approved_by" integer, "invoice_print_count" integer not null default '0', "invoice_reprint_status" varchar not null default 'none', "status_update_requested" varchar, "address" varchar, "tax_percentage" numeric not null default '0', "tax_amount" numeric not null default '0', foreign key("room_id") references rooms("id") on delete set null on update no action, foreign key("discount_approved_by") references "users"("id") on delete set null);
CREATE TABLE IF NOT EXISTS "notifications" ("id" varchar not null, "type" varchar not null, "notifiable_type" varchar not null, "notifiable_id" integer not null, "data" text not null, "read_at" datetime, "created_at" datetime, "updated_at" datetime, primary key ("id"));
CREATE TABLE IF NOT EXISTS "event_bookings" ("id" integer primary key autoincrement not null, "customer_name" varchar not null, "customer_email" varchar not null, "customer_phone" varchar not null, "event_type" varchar not null, "event_date" date not null, "start_time" time not null, "end_time" time not null, "guests" integer not null, "message" text, "status" varchar not null default ('pending'), "total_price" numeric, "created_at" datetime, "updated_at" datetime, "invoice_print_count" integer not null default ('0'), "invoice_reprint_status" varchar not null default ('none'), "discount_percentage" numeric not null default '0', "discount_status" varchar check ("discount_status" in ('none', 'pending', 'approved', 'rejected')) not null default 'none', "discount_approved_by" integer, "conflict_status" varchar check ("conflict_status" in ('none', 'requested', 'approved', 'rejected')) not null default 'none', "tax_percentage" numeric not null default '0', "tax_amount" numeric not null default '0', foreign key("discount_approved_by") references "users"("id") on delete set null);
CREATE TABLE IF NOT EXISTS "home_events" ("id" integer primary key autoincrement not null, "title" varchar not null, "description" text, "image_path" varchar, "icon" varchar not null default 'heart', "sort_order" integer not null default '0', "created_at" datetime, "updated_at" datetime);
DELETE FROM sqlite_sequence;
CREATE UNIQUE INDEX "users_email_unique" on "users" ("email");
CREATE INDEX "sessions_user_id_index" on "sessions" ("user_id");
CREATE INDEX "sessions_last_activity_index" on "sessions" ("last_activity");
CREATE INDEX "jobs_queue_index" on "jobs" ("queue");
CREATE UNIQUE INDEX "failed_jobs_uuid_unique" on "failed_jobs" ("uuid");
CREATE UNIQUE INDEX "rooms_slug_unique" on "rooms" ("slug");
CREATE UNIQUE INDEX "content_settings_key_unique" on "content_settings" ("key");
CREATE INDEX "notifications_notifiable_type_notifiable_id_index" on "notifications" ("notifiable_type", "notifiable_id");
COMMIT;
